import { useState } from 'react';
import './App.css';

function Game2(){
    return(
        <Board />
    );
}

function Board(){
    const [isNext, setIsNext] = useState(true);
    const [squares, setSquares] = useState(Array(9).fill(null));

    function handleSquare(i){
        if(squares[i] || calculateWinner(squares)){
            return;
        }

        const newSquare = squares.slice();
        newSquare[i] = isNext ? "X":"O";
        setSquares(newSquare);
        setIsNext(!isNext);
    }

    const winner = calculateWinner(squares);
    let status;
    if(winner){
        status = "Winner: "+ winner;
    }else{
        status = "Next Player: " + (isNext ? "X":"O");
    }

    return(
        <>
            <div>{status}</div>
           <div className='board-row'>
                {Array(3).fill(null).map(
                    (ele, index) => <Square value={squares[index]} squareClick={() => handleSquare(index)}/>)}
            </div>
            <div className='board-row'>
                {Array(3).fill(null).map(
                    (ele, index) => <Square value={squares[index+3]} squareClick={() => handleSquare(index+3)}/>)}
                {/* <Square value={squares[3]} squareClick={() => handleSquare(3)}/>
                <Square value={squares[4]} squareClick={() => handleSquare(4)}/>
                <Square value={squares[5]} squareClick={() => handleSquare(5)}/> */}
            </div>
            <div className='board-row'>
                <Square value={squares[6]} squareClick={() => handleSquare(6)}/>
                <Square value={squares[7]} squareClick={() => handleSquare(7)}/>
                <Square value={squares[8]} squareClick={() => handleSquare(8)}/>
            </div>
        </>
    );
}

function Square({value, squareClick}){
    return(
        <div className='square' onClick={squareClick}>
            {value}
        </div>
    );
}

function calculateWinner(squares){
    const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6]
      ];

    for(let i=0; i<lines.length; i++){
        const [a,b,c] = lines[i]
        if(squares[a] && squares[a] === squares[b] && squares[a] === squares[c]){
            return squares[a];
        }
    }
    return null;
}


export default Game2;
