import { useState } from 'react';
import './App.css';

function App(){
  return(
    <div>
      <Board />
    </div>
  );
}

function Board(){
  const[squares, setSquares] = useState(Array(9).fill(null));


  function handleOnAdd(i){
    const nextSquares = squares.slice();
    setSquares(nextSquares => nextSquares[i]="X")
  }

  return(
    <>
      <div className='board-row'>
        <Square value={squares[0]} handleOnAdd={() => handleOnAdd(0)}/>
        <Square value={squares[1]} handleOnAdd={() => handleOnAdd(1)}/>
        <Square value={squares[2]} handleOnAdd={() => handleOnAdd(2)}/>
      </div>
      <div className='board-row'>
        <Square value={squares[3]} handleOnAdd={() => handleOnAdd(3)}/>
        <Square value={squares[4]} handleOnAdd={() => handleOnAdd(4)}/>
        <Square value={squares[5]} handleOnAdd={() => handleOnAdd(5)}/>
      </div>
      <div className='board-row'>
        <Square value={squares[6]} handleOnAdd={() => handleOnAdd(6)}/>
        <Square value={squares[7]} handleOnAdd={() => handleOnAdd(7)}/>
        <Square value={squares[8]} handleOnAdd={() => handleOnAdd(8)}/>
      </div>
    </>
  );
}

function Square({value, handleOnAdd}){
  return(
    <button className='square' onClick={handleOnAdd}>{value}</button>
  );
}

export default App;
