import { useState } from 'react';
import './App.css';

function Game4(){
    return(
        <>
            <Board />
        </>
    );
}

function Board(){

    const [isNext, setIsNext] = useState(true);
    const [squares, setSquares] = useState(Array(9).fill(null));

    function handleClick(i){
        if(squares[i] || calculateWinner(squares)){
            return;
        }

        const newSquare = squares.slice();

        newSquare[i] = isNext ? "X" : "O";
        setSquares(newSquare);
        setIsNext(!isNext);

    }

    const winner = calculateWinner(squares);
    let status;

    if(winner.winner){
        status="Winner: " +winner.winner;
    }else{
        status="Next is: " +isNext ? "X":"O";
    }

    return(
        // <>
        //     <div>{status}</div>
        //     {[0,3,6].map((start) => (
        //         <div className='board-row'>
        //             {Array(3).fill(null).map((_, index) => (
        //                 <Square value={squares[start+index]} onClickSquare={() => handleClick(start+index)}/>
        //             ))}
        //         </div>
        //     ))}
        // </>
        [0,3,6].map((start) => (
            <div className='board-row'>
                {Array(3).fill(null).map((_, index) => (
                    <Square value={squares[start+index]} onClickSquare={() => handleClick(start+index)}/>
                ))}
            </div>
        ))
    );
}

function Square({value, onClickSquare}){
    return(
        <div className='square' onClick={onClickSquare}>
            {value}
        </div>
    );
}

function calculateWinner(squares){
    const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6]
      ];
    for(let i=0; i<lines.length; i++){
        const [a,b,c] = lines[i];
        if(squares[a] && squares[a] === squares[b] && squares[a] === squares[c]){
            return {winner: squares[a], inde: lines[i]};
        }
    }
    return null;
}

export default Game4;
