import { useState } from 'react';
import './App.css';

function Game3(){
    return(
        <Board />
    );
}

function Board(){
    const [isNext, setIsNext] = useState(true);
    const [squares, setSquares] = useState(Array(9).fill(null));

    function handleAddClick(i){
        const newSquares = squares.slice();

        newSquares[i] = isNext ? "X":"O";
        setSquares(newSquares);
        setIsNext(!isNext);
    }

    const winner = calculateWinner(squares);
    let status;

    if(winner){
        status = "Winner: " +winner;
    }else{
        status = "Next Player: " +(isNext ? "X":"O");
    }



    return(
        <>
            <div>{status}</div>
            <div className='board-row'>
            {[0,3,6].map((start) => {
                <div key={start} className='board-row'>
                {Array(9).fill(null).map((_,index) => {
                    <Square key={start+index} value={squares[start+index]} addClick={() => handleAddClick(start+index)}/>
                })}
            </div>
            })}
            </div>
        </>
    );
}

function Square({value, addClick}){
    return(
        <div className='square' onClick={addClick}>{value}</div>
    );
}

function calculateWinner(squares){
    const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6]
      ];
    for(let i=0; i<lines.length; i++){
        const [a,b,c] = lines[i];
        if(squares[a] && squares[a] === squares[b] && squares[a] === squares[c]){
            return squares[a];
        }
    }
    return null;
}

export default Game3;
