import { useState } from "react";

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false},
  { id: 3, description: "T-shirts", quantity: 4, packed: true},
];

export default function App(){
  return(
    <div className="app">
      <Logo />
      <Form />
      <PackagingLists items={initialItems}/>
      <Stats />
    </div>
  )
}

function Logo(){
  return(
    <h1>Faraway Island </h1>
  )
}

function Form(){
  const [description, setDescription] = useState('');
  const [quantity, setQuantity] = useState();

  function handleSubmit(e){
    e.preventDefault();
  }

  return(
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your trip</h3>
      <select value={quantity}>
        {Array.from({length: 20}, (_,i) => i+1).map(x => <option value={x} key={x}>{x}</option>)}
      </select>
      <input type="text" placeholder="Item..." value={description} onChange={(e) => setDescription(e.target.value)}/>
      <button>Add</button>
    </form>
  )
}

function PackagingLists(){
  return(
    <div className="list">
      <ul>
        {initialItems.map((item) => <Item key={item.id} item={item}/>)}
      </ul>
    </div>
  )
}

function Item({item}){
  return(  
      <li>
        <span style={item.packed ? {textDecoration: "line-through"} : {}}>{item.quantity} {item.description}</span>
        <button className="delete-button">X</button>
      </li>
  )
}

function Stats(){
  return(
    <footer className="stats">
      <em>You have X items in your list, you already packed X (x%)</em>
    </footer>
  )
}
